import React, { useState } from "react";
import "./Expenses.css";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter";

const Expenses = (props) => {
  const [filteredYear, setFilteredYear] = useState("2020"); // initialize default values as 2020
  const filterChangeHandler = (selectedYear) => {
    // selectedYear: parameter
    setFilteredYear(selectedYear);
    console.log("hhh");
    console.log(selectedYear);
  };

  const allExpenses = props.items;

  const filteredExpenses = allExpenses.filter((allExpense) => {
    return allExpense.date === filteredYear;
  });

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          selected={filteredYear} // initialize default values as 2020
          onChangeFilter={filterChangeHandler}
        />
        {filteredExpenses.map((allExpense) => {
          return (
            <ExpenseItem
              key={allExpense.id}
              title={allExpense.title}
              amount={allExpense.amount}
              date={allExpense.date}
            />
          );
        })}
      </Card>
    </div>
  );
};

export default Expenses;
