import React, { useState } from "react";
import "./Expenses.css";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter";

const Expenses = (props) => {
  const [filteredYear, setFilteredYear] = useState("2020"); // initialize default values as 2020
  const filterChangeHandler = (selectedYear) => {
    // selectedYear: parameter
    setFilteredYear(selectedYear);
    console.log("hhh");
    console.log(selectedYear);
  };

  const allExpense = props.items.map((expense) => (
    <ExpenseItem
      key={expense.id}
      title={expense.title}
      amount={expense.amount}
      date={expense.date}
    />
  ));

  console.log("all the expenses:");
  console.log(allExpense);

  const filteredExpense = allExpense.filter((expense) => {
    return expense.date === "2021";
  });

  console.log(filteredExpense);

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          selected={filteredYear} // initialize default values as 2020
          onChangeFilter={filterChangeHandler}
        />
        {props.items.map((expense) => (
          <ExpenseItem
            key={expense.id}
            title={expense.title}
            amount={expense.amount}
            date={expense.date}
          />
        ))}
      </Card>
    </div>
  );

  // const filtered = expenses.filter((expense) => {
  //   return expense.date.getFullYear === "2021";
  // });

  // console.log("wtf");
  // console.log(filtered);
};

export default Expenses;
