import React from "react";
import "./ExpensesFilter.css";

const ExpensesFilter = () => {
  const yearOneChangeHandler = (event) => {
    event.target.value;
  };
  const yearTwoChangeHandler = (event) => {
    setYearTwo(event.target.value);
  };
  const yearThreeChangeHandler = (event) => {
    setYearThree(event.target.value);
  };
  const yearFourChangeHandler = (event) => {
    setYearFour(event.target.value);
  };
  return (
    <div className="expenses-filter">
      <div className="expenses-filter__control">
        <label>Filter by year</label>
        <select>
          <option value="2022" onChange={yearOneChangeHandler}>
            2022
          </option>
          <option value="2021" onChange={yearTwoChangeHandler}>
            2021
          </option>
          <option value="2020" onChange={yearThreeChangeHandler}>
            2020
          </option>
          <option value="2019" onChange={yearFourChangeHandler}>
            2019
          </option>
        </select>
      </div>
    </div>
  );
};

export default ExpensesFilter;
