import React, { useState } from "react";
import "./ExpensesFilter.css";

const ExpensesFilter = (props) => {
  const [year, setYear] = useState("");

  const yearChangeHandler = (event) => {
    setYear(event.target.value);
  };

  const dropdownChangeHandler = (event) => {
    event.preventDefault();
    const selectedYear = { year };
    props.onSaveSelectedYear(selectedYear);
  };

  return (
    <div className="expenses-filter">
      <div className="expenses-filter__control">
        <label>Filter by year</label>
        <select onChange={dropdownChangeHandler}>
          <option value="2022" onChange={yearChangeHandler}>
            2022
          </option>
          <option value="2021" onChange={yearChangeHandler}>
            2021
          </option>
          <option value="2020" onChange={yearChangeHandler}>
            2020
          </option>
          <option value="2019" onChange={yearChangeHandler}>
            2019
          </option>
        </select>
      </div>
    </div>
  );
};

export default ExpensesFilter;
