import React, { useState } from "react";
import "./NewForm.css";

const ExpenseForm = () => {
  // The React useState Hook allows us to track state in a function component. State generally refers to data or properties that need to be tracking in an application.
  // 1. method one
  // const [enteredTitle, setEnteredTitle] = useState("");
  // const [enteredAmount, setEnteredAmount] = useState("");
  // const [enteredData, setEnteredDate] = useState("");

  // 2. method two
  const [userInput, setUserInput] = useState({
    enteredTitle: "",
    enteredAmount: "",
    enteredData: "",
  });

  const titleChangeHandler = (event) => {
    // 1. method one
    //setEnteredTitle(event.target.value); // the value would always be a string

    // 2. method two
    setUserInput((prevState) => {
      return { ...prevState, enteredTitle: event.target.value };
      // ... takes an object and pull out all the key value pairs and add them to the new object
    });
  };

  const amountChangeHandler = (event) => {
    // 1. method one
    //setEnteredAmount(event.target.value);

    // 2. method two
    setUserInput((prevState) => {
      return { ...prevState, enteredAmount: event.target.value };
    });
  };

  const dateChangeHandler = (event) => {
    // 1. method one
    //setEnteredDate(event.target.value);

    // 2. method two
    setUserInput((prevState) => {
      return { ...prevState, enteredDate: event.target.value };
    });
  };

  return (
    <form>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input type="text" onChange={titleChangeHandler}></input>
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input
            type="number"
            min="0.01"
            step="0.01"
            onChange={amountChangeHandler}
          ></input>
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input
            type="date"
            min="2019-01-01"
            max="2024-11-20"
            onChange={dateChangeHandler}
          ></input>
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="submit">Add Expense Asshole</button>
      </div>
    </form>
  );
};

export default ExpenseForm;
